page_settings.custom_css:
#qc-header {
    background-color: transparent;
    z-index: 2;
}
#testimonial .swiper-container{
    padding-bottom: 0;
}

#testimonial .elementor-swiper-button > i {
    display: none;
}
#testimonial .elementor-swiper-button:before{
    width: 30px;
    height: 24px;
    opacity: .6;
    transition: all 0.5s;
    -webkit-transition: all 0.5s;
    -moz-transition: all 0.5s;
    -o-transition: all 0.5s;
}
#testimonial .elementor-swiper-button:hover:before{
    opacity: 1;
}
#testimonial .elementor-swiper-button-prev:before {
    content: url(/wp-content/uploads/2020/07/QE-07.svg);
}
#testimonial .elementor-swiper-button-next:before{
    content: url(/wp-content/uploads/2020/07/QE-06.svg);
}
#abs-testimonial {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    top: -40px;
}
#stat .elementor-icon-list-item{
    align-items: center;
    -webkit-align-items: center;
}
#stat .elementor-icon-list-item:first-child svg{
    width:45px;
}
#stat-wrap .elementor-container:before {
    content: '';
    background-color: #fff;
    width: 200%;
    height: calc(100% + 45px);
    position: absolute;
    top: -30px;
    left: -140px;
    box-shadow: 2px 8px 10px 0px rgba(0,0,0,0.2);
}

.gform_wrapper .gfield{
    margin-top: 0 !important;
}
.address_zip {
    min-height: 0px !important;
    width: 100% !important;
    padding: 0px !important;
}
.gf-terms .pp-info-list-item-inner{
    margin:0 auto;
}
.pp-gravity-form .gform_wrapper {
    margin-top: 0px !important;
}
.gform_wrapper .chosen-container-multi .chosen-choices{
    border: 0;
}
.chosen-search-input {
    margin: 0px !important;
}
.gform_wrapper li.gfield.gfield_error {
    padding-top: 0;
    border: 0;
}
.gform_wrapper .validation_message {
    padding: 2px 5px 0px !important;
}
.gform_wrapper div.validation_error{
    line-height: 1.2;
}
@media only screen and (max-width:1024px){
    #testimonial{
        background-position: top center;
        background-size: cover;
    }
    #stat .elementor-icon-list-item:first-child svg{
    width:35px;
    }
    #stat-wrap .elementor-container:before{
        display:none;
    }
}
@media only screen and (max-width:767px){
    #qc-header {
    background-color:  #122649;
}

  .sm-nobr br{
    display: none;
}
#testimonial {
    background-position: right center;
}
#testimonial .elementor-swiper-button-prev:before {
    left:-10px;
}
#testimonial .elementor-swiper-button-next:before{
    right:-10px;
}
#testimonial .elementor-swiper-button:before{
    width: 24px;
    height: 20px;
}
#stat .elementor-inline-items{
    -webkit-box-pack: start;
    -moz-flex-pack: start;
    -ms-flex-pack: start;
    justify-content: flex-start;
}
#stat-wrap{
    background-color:#fff;
}
#stat .elementor-icon-list-item{
    margin-top:10px;
}
#stat .elementor-icon-list-item:first-child svg {
    width: 30px;
}
.sm-wrap .elementor-container {
    max-width: 420px;
}
}

@media only screen and (min-width:641px){
    .gf-tag, .gf-terms{
        padding-right:16px;
    }
    .gform_wrapper div.validation_error{
        width: calc(100% - 16px);
    }
}
