settings.custom_css:
.quote-mark .elementor-testimonial__footer {
    position: relative;
}
.quote-mark .elementor-testimonial__footer:after {
    content: '';
    width: 65px;
    height: 65px;
    position: absolute;
    background-image: url(/wp-content/uploads/2020/05/APE_quotation-mark.svg);
    right: 20px;
    top: -20px;
    background-repeat: no-repeat;
    background-size: contain;
}


@media only screen and (max-width: 767px){
    .ioswip .elementor-swiper-button {
    display: none;
}
.ioswip .elementor-testimonial{
    flex-direction: column-reverse;
    -webkit-flex-direction: column-reverse;
    -ms-flex-direction: column-reverse;
}
.ioswip .elementor-testimonial__content{
    text-align:center;
}
.ioswip .elementor-testimonial__image{
    margin-bottom:15px;
}
}

@media only screen and (max-width:479px){
    .ioswip .swiper-slide {
        padding:0px;
    }
    .quote-mark .elementor-testimonial__footer:after{
    top: 90px;
    left: 0px;
    width: 45px;
    height: 45px;
        right: auto;

    }
}

